import React, { useCallback, useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import AppLayout from '../components/AppLayout';
import { Row, Col, DatePicker, Space, Card, Modal, Button, Input } from 'antd';
import Router from 'next/router';
import { LOAD_DIARY_REQUEST } from '../reducers/diary';

const Diary = () => {

    const dispatch = useDispatch();

    const { post } = useSelector((state) => state.diary);
    const { isLoggedIn } = useSelector((state) => state.user);

    useEffect(() => {
        if(!isLoggedIn){
            Router.push('/');
        }
    });

    if (!isLoggedIn) {
        return '내 정보 로딩중...';
    }

    //Add Post State
    const [addTitle, setAddTitle] = useState('');//Add Post Title
    const [addContent, setAddContent] = useState('');//Add Post Contnet
    //Open Add Post State
    const [visible, setVisible] = useState(false);
    //Date State
    const [selectDate, setSelectDate] = useState('날짜 선택!');

    //Date Pick
    const onChangeCal = useCallback((date, dateString) => {
        setSelectDate(dateString);
        console.log(selectDate);
    }, [selectDate]);

    // Open Add Post
    const onOpenAddPost = useCallback(() => {
        setVisible(true);
    }, [visible]);

    // Close Add Post
    const onCancel = useCallback(() => {
        setVisible(false);
        setAddTitle('');
    }, [visible, addTitle]);

    // Submit Add Post
    const onSubmitPost = useCallback(() => {
        //제목 글내용 날짜 올리기
        setVisible(false);
        setAddTitle('');
        setAddContent('');
    }, [visible, addTitle]);

    // Add Post Tile Change
    const onChangeTitle = useCallback((e) => {
        setAddTitle(e.target.value);
    }, [addTitle]);

    // Add Post Content Change
    const onChangeContent = useCallback((e) => {
        setAddContent(e.target.value);
    }, [addContent]);

    //Find Post
    const onFindPost = useCallback(() => {
        const date = selectDate;        
        if(date === "날짜 선택!"){
           return alert("날짜를 선택하세요!");
        }
        dispatch({
            type: LOAD_DIARY_REQUEST,
            data: date,
        });
    }, [selectDate]);

    return(
        <>
            <AppLayout>
                <Row>
                    <Col xs={24} md={6}>

                    </Col>
                    <Col xs={24} md={12}>
                        <Space direction="horizontal">
                            <DatePicker style={{marginTop: '10px', marginBottom: '30px'}} placeholder={selectDate} onChange={onChangeCal} />
                        </Space>
                        <Button onClick={onFindPost}>조회</Button>
                        <Button onClick={onOpenAddPost}>글쓰기</Button>
                        <Modal
                        visible={visible}
                        title={<Input 
                            style={{width: '70%'}} 
                            value={addTitle} 
                            placeholder="제목을 입력하세요" 
                            onChange={onChangeTitle}    
                        />}
                        onOk={onSubmitPost}
                        onCancel={onCancel}
                        footer={[<Button key="back" onClick={onCancel}>Return</Button>,
                                <Button key="submit" type="primary" /*loading={}*/ onClick={onSubmitPost}>Submit</Button>]} >
                                <DatePicker style={{marginBottom: '10px'}} placeholder={selectDate} onChange={onChangeCal} />
                                <Input.TextArea 
                                rows={10} 
                                placeholder="오늘의 기억남는 하루를 남겨보세요 :)"
                                onChange={onChangeContent}
                                value={addContent}
                                />
                        </Modal>
                        <Card style={{margin: '20px'}} title={post[0].title} bordered={false}>
                            <p>{post[0].content}</p>
                        </Card>
                    </Col>
                    <Col xs={24} md={6}>

                    </Col>
                </Row>
            </AppLayout>
        </>
    );
}

export default Diary;
