import produce from '../util/produce';

//user 더미 데이터
const dummyUser = {
    id: 1,
    nickname: 'Solac',
  };

  export const initialState = {
      isLoggedIn: false,// Log or Not Log
      isLogLoding: false, // Login Lodaing
      isLogError: null, //LogIn Error

      user: null,// User Info
  };

export const LOG_IN_REQUEST = 'LOG_IN_REQUEST'; // 액션의 이름
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS'; // 액션의 이름
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE'; // 액션의 이름

export const LOG_OUT_REQUEST = 'LOG_OUT_REQUEST'; // 액션의 이름
export const LOG_OUT_SUCCESS = 'LOG_OUT_SUCCESS'; // 액션의 이름
export const LOG_OUT_FAILURE = 'LOG_OUT_FAILURE'; // 액션의 이름

const reducer = (state = initialState, action) => produce(state, (draft) => {
    switch(action.type){
        case LOG_IN_REQUEST:{
            draft.isLogLoding = true; // LogIn Loading On
            break;
        }
        case LOG_IN_SUCCESS: {
            draft.isLogLoding = false; //LogIn Loading Off
            draft.isLoggedIn = true; //LogIn Done
            draft.user = dummyUser; // UserData Insert
            break;
        }
        case LOG_IN_FAILURE: {
            draft.isLogLoding = false; //LogIn Loading Off
            draft.isLogError = action.data;// Error Insert
            break;
        }
        case LOG_OUT_REQUEST:{
            draft.isLoggedIn = false;
            draft.user = null;
            break;
        }
    }
})

export default reducer;
