import React, { useCallback, useEffect, useState } from 'react';
import AppLayout from '../components/AppLayout';
import { Row, Col, DatePicker, Space, Card, Modal, Button } from 'antd';
import Input from 'antd/lib/input';
const Diary = () => {
    //Add Post State
    const [addTitle, setAddTitle] = useState('');//Add Post Title
    const [addContent, setAddContent] = useState('');//Add Post Contnet
    //Open Add Post State
    const [visible, setVisible] = useState(false);
    //Date State
    const [selectDate, setSelectDate] = useState('9999-01-01');

    //Date Pick
    const onChangeCal = useCallback((date, dateString) => {
        setSelectDate(dateString);
        console.log(selectDate);
    }, [selectDate]);

    // Open Add Post
    const onOpenAddPost = useCallback(() => {
        setVisible(true);
    }, [visible]);

    // Close Add Post
    const onCancel = useCallback(() => {
        setVisible(false);
        setAddTitle('');
    }, [visible, addTitle]);

    // Submit Add Post
    const onSubmitPost = useCallback(() => {
        setVisible(false);
        setAddTitle('');
    }, [visible, addTitle]);

    // Add Post Tile Change
    const onChangeTitle = useCallback((e) => {
        setAddTitle(e.target.value);
    }, [addTitle]);

    return(
        <>
            <AppLayout>
                <Row>
                    <Col xs={24} md={6}>

                    </Col>
                    <Col xs={24} md={12}>
                        <Space direction="horizontal">
                            <DatePicker style={{marginTop: '10px', marginBottom: '30px'}} placeholder={selectDate} onChange={onChangeCal} />
                        </Space>
                        <Button onClick={onOpenAddPost}>글쓰기</Button>
                        <Modal
                        visible={visible}
                        title={<Input 
                            style={{width: '70%'}} 
                            value={addTitle} 
                            placeholder="제목을 입력하세요" 
                            onChange={onChangeTitle}    
                        />}
                        onOk={onSubmitPost}
                        onCancel={onCancel}
                        footer={[<Button key="back" onClick={onCancel}>Return</Button>,
                                <Button key="submit" type="primary" /*loading={}*/ onClick={onSubmitPost}>Submit</Button>]} >

                        </Modal>
                        <Card style={{margin: '20px'}} title="제목부분" bordered={false}>
                            <p>일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기
                            일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기일기
